import React, { useState, useEffect } from 'react';
import './App.css';
import Login from './Login';

function App() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [selectedJob, setSelectedJob] = useState(null);
  const [applicationData, setApplicationData] = useState({
    name: '',
    email: '',
    phone: '',
    experience: '',
    resume: ''
  });
  const [applicationsSubmitted, setApplicationsSubmitted] = useState([]);
  const [loggedInStudent, setLoggedInStudent] = useState(null);

  const API_BASE = 'http://localhost:5000/api';

  useEffect(() => {
    if (loggedInStudent) {
      fetchJobs();
      setApplicationData(prev => ({
        ...prev,
        name: loggedInStudent.name,
        email: loggedInStudent.email
      }));
    }
  }, [loggedInStudent]);

  const fetchJobs = async () => {
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE}/jobs`);
      const data = await response.json();
      setJobs(data);
      setError('');
    } catch (err) {
      setError('Failed to fetch jobs. Check backend connection.');
    } finally {
      setLoading(false);
    }
  };

  const handleApplyJob = (job) => {
    setSelectedJob(job);
  };

  const handleSubmitApplication = (e) => {
    e.preventDefault();
    if (!applicationData.name || !applicationData.email || !applicationData.phone) {
      setError('Please fill in all fields');
      return;
    }
    
    const application = {
      jobId: selectedJob.id,
      jobTitle: selectedJob.title,
      company: selectedJob.company,
      ...applicationData,
      appliedDate: new Date().toLocaleDateString()
    };
    
    setApplicationsSubmitted([...applicationsSubmitted, application]);
    setError('');
    setSelectedJob(null);
    setApplicationData({ name: '', email: '', phone: '', experience: '', resume: '' });
    alert(`Successfully applied to ${selectedJob.title} at ${selectedJob.company}!`);
  };

  return (
    <div className="App">
      <header className="header">
        <h1>ðŸ’¼ Placement Portal</h1>
        <p className="subtitle">Find Your Dream Job</p>
      </header>

      {error && <div className="error-message">{error}</div>}

      <div className="container">
        <div className="jobs-section">
          <h2>Available Positions</h2>
          {loading && <p className="loading">Loading jobs...</p>}
          {jobs.length === 0 && !loading && <p>No jobs available</p>}
          
          <div className="jobs-grid">
            {jobs.map((job) => (
              <div key={job.id} className="job-card">
                <div className="job-header">
                  <h3>{job.title}</h3>
                  <span className="company-badge">{job.company}</span>
                </div>
                <p className="tagline">"{job.tagline}"</p>
                <p className="description">{job.description}</p>
                <div className="requirements">
                  <strong>Requirements:</strong>
                  <p>{job.requirements}</p>
                </div>
                <button 
                  className="apply-btn"
                  onClick={() => handleApplyJob(job)}
                >
                  Apply Now
                </button>
              </div>
            ))}
          </div>
        </div>

        {selectedJob && (
          <div className="application-modal">
            <div className="modal-content">
              <button className="close-btn" onClick={() => setSelectedJob(null)}>Ã—</button>
              <h2>Apply for {selectedJob.title}</h2>
              <p className="modal-company">at {selectedJob.company}</p>
              
              <form onSubmit={handleSubmitApplication} className="application-form">
                <div className="form-group">
                  <label>Full Name *</label>
                  <input
                    type="text"
                    placeholder="Enter your full name"
                    value={applicationData.name}
                    onChange={(e) => setApplicationData({ ...applicationData, name: e.target.value })}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Email Address *</label>
                  <input
                    type="email"
                    placeholder="Enter your email"
                    value={applicationData.email}
                    onChange={(e) => setApplicationData({ ...applicationData, email: e.target.value })}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Phone Number *</label>
                  <input
                    type="tel"
                    placeholder="Enter your phone number"
                    value={applicationData.phone}
                    onChange={(e) => setApplicationData({ ...applicationData, phone: e.target.value })}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Years of Experience</label>
                  <input
                    type="number"
                    placeholder="e.g., 2"
                    value={applicationData.experience}
                    onChange={(e) => setApplicationData({ ...applicationData, experience: e.target.value })}
                  />
                </div>

                <div className="form-group">
                  <label>Cover Letter / Resume Summary</label>
                  <textarea
                    placeholder="Tell us why you're a great fit for this role..."
                    rows="5"
                    value={applicationData.resume}
                    onChange={(e) => setApplicationData({ ...applicationData, resume: e.target.value })}
                  />
                </div>

                <button type="submit" className="submit-btn">Submit Application</button>
              </form>
            </div>
          </div>
        )}

        {applicationsSubmitted.length > 0 && (
          <div className="applications-section">
            <h2>Your Applications ({applicationsSubmitted.length})</h2>
            <div className="applications-list">
              {applicationsSubmitted.map((app, index) => (
                <div key={index} className="application-card">
                  <h4>{app.jobTitle}</h4>
                  <p><strong>Company:</strong> {app.company}</p>
                  <p><strong>Applied On:</strong> {app.appliedDate}</p>
                  <p><strong>Status:</strong> <span className="status-badge">Pending Review</span></p>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
